import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { GiAnvilImpact } from "react-icons/gi";
import { FaBars, FaTimes } from "react-icons/fa";
import { Button } from "./Button";
import "./Navbar.css";
import { IconContext } from "react-icons/lib";

function NavBar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener("resize", showButton);

  return (
    <>
      <IconContext.Provider value={{ color: "#fff" }}>
        <div className="navbar">
          <div className="navbar-container container">
            <Link to="/"   className="navbar-logo" onClick={closeMobileMenu}>
              <GiAnvilImpact className="navbar-icon" />
              Blacksmith
            </Link>
            <div className="menu-icon" onClick={handleClick}>
              {click ? <FaTimes /> : <FaBars />}
            </div>
            <ul className={click ? "nav-menu active" : "nav-menu"}>
              <li className="nav-item">
                <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="/about"
                  className="nav-links"
                  onClick={closeMobileMenu}
                >
                  About
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="/pieces"
                  className="nav-links"
                  onClick={closeMobileMenu}
                >
                  Art
                </Link>
              </li>
              <li className="nav-btn">
                {button ? (
                  <a href="mailto:tmcdonough08@gmail.com" 
                    className="btn-link"
                    onClick={closeMobileMenu}
                  >
                    <Button buttonStyle="btn--outline">Connect</Button>
                  </a>
                ) : (
                  <a href="mailto:tmcdonough08@gmail.com" className="btn-link">
                    <Button buttonStyle="btn--outline" buttonSiz="btn--mobile">
                      Connect
                    </Button>
                  </a>
                )}
              </li>
            </ul>
          </div>
        </div>
      </IconContext.Provider>
    </>
  );
}

export default NavBar;
